# Holds info about OIDs.
# Each OID info is a named tuple with the following attributes:
# oid - the OID number
# type - type of OID
# name - description of OID
# doc - reference document of OID
# obsolete - boolean to define an historic state of the OID
#
# Source of information is IANA ldap-parameters.txt, oid-registry and products documentation as of 2013.08.21

def constantToOidKind(oidKind):
    if oidKind == 0:
        return 'Control'
    elif oidKind == 1:
        return 'Extension'
    elif oidKind == 2:
        return 'Feature'
    elif oidKind == 3:
        return 'Unsolicited Notice'
    elif oidKind == 4:
        return 'Attribute Type'
    elif oidKind == 5:
        return 'DIT Content Rule'
    elif oidKind == 6:
        return 'LDAP URL Extension'
    elif oidKind == 7:
        return 'Family'
    elif oidKind == 8:
        return 'Matching Rule'
    elif oidKind == 9:
        return 'Name Form'
    elif oidKind == 10:
        return 'Object Class'
    elif oidKind == 11:
        return 'Administrative Role'
    elif oidKind == 12:
        return 'LDAP Syntax'
    else:
        return 'Unknown'


class OidInfo(namedtuple('OidInfo', 'oid, kind, name, docs')):
    def __str__(self):
        r = self.oid + ' - '
        if self.name:
            r += (', '.join(self.name)) if isinstance(self.name, list) else  self.name + ' - '
        r += constantToOidKind(self.kind) + ' - ' if self.kind is not None else ''
        r += self.docs + ' - ' if self.docs else ''

        return r[:-3]

    def __repr__(self):
        return self.__str__()


